#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "1F72443D6AC449C7B959047522ED087B", standard_notation: "CCSS.Math.Content.3.OA.A.1", alt_standard_notation: "3.OA.1", description: "Interpret products of whole numbers, e.g., interpret 5 × 7 as the total number of objects in 5 groups of 7 objects each."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "13E091EB7A6C41949CFF99799A39D3C2", standard_notation: "CCSS.Math.Content.3.OA.A", alt_standard_notation: nil, description: "Represent and solve problems involving multiplication and division."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "D0714BF0DFE401311CE968A86D17958E", standard_notation: "Operations and Algebraic Thinking", alt_standard_notation: nil, description: "Operations and Algebraic Thinking"},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "C495D75692F64582AD468AD17E059BE7", standard_notation: "CCSS.Math.Practice.MP8", alt_standard_notation: "MP.8", description: "Look for and express regularity in repeated reasoning."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "1C4CD75AFB0243B8A7F22CCF3A0B1323", standard_notation: "CCSS.Math.Practice.MP7", alt_standard_notation: "MP.7", description: "Look for and make use of structure."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "57E5B7F6A0D840489E58E4BC4CE2EAA7", standard_notation: "CCSS.Math.Practice.MP6", alt_standard_notation: "MP.6", description: "Attend to precision."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "87EC2B4182B94957AB8BC329C81D7DB1", standard_notation: "CCSS.Math.Practice.MP5", alt_standard_notation: "MP.5", description: "Use appropriate tools strategically."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "F2530A359AEE44E1AA976FF040D507AF", standard_notation: "CCSS.Math.Practice.MP4", alt_standard_notation: "MP.4", description: "Model with mathematics."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "B64364515C5A4A079463D527FDC7A93F", standard_notation: "CCSS.Math.Practice.MP3", alt_standard_notation: "MP.3", description: "Construct viable arguments and critique the reasoning of others."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "1EA046F9136E4B4F9F99F54320F342CE", standard_notation: "CCSS.Math.Practice.MP2", alt_standard_notation: "MP.2", description: "Reason abstractly and quantitatively."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "C0F6858936EE4E45A1BC28BF9282FFEF", standard_notation: "CCSS.Math.Practice.MP1", alt_standard_notation: "MP.1", description: "Make sense of problems and persevere in solving them."},
#   {name: nil, grade: "Grade 3", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-03", api_standard_id: "D0868B50DFE401311CEE68A86D17958E", standard_notation: "Standards for Mathematical Practice", alt_standard_notation: nil, description: "Standards for Mathematical Practice"},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "C374A9ECF108450BB9790DDB560BBB27", standard_notation: "CCSS.Math.Content.4.NF.C.7", alt_standard_notation: "4.NF.7", description: "Compare two decimals to hundredths by reasoning about their size. Recognize that comparisons are valid only when the two decimals refer to the same whole. Record the results of comparisons with the symbols >, =, or <, and justify the conclusions, e.g., by using a visual model."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "C374A9ECF108450BB9790DDB560BBB26", standard_notation: "CCSS.Math.Content.4.NF.C.6", alt_standard_notation: "4.NF.6", description: "Use decimal notation for fractions with denominators 10 or 100."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "C374A9ECF108450BB9790DDB560BBB25", standard_notation: "CCSS.Math.Content.4.NF.C.5", alt_standard_notation: "4.NF.5", description: "Express a fraction with denominator 10 as an equivalent fraction with denominator 100, and use this technique to add two fractions with respective denominators 10 and 100."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "30C671F2536E4F57B7A32A782A6E00B9", standard_notation: "CCSS.Math.Content.4.NF.C", alt_standard_notation: nil, description: "Understand decimal notation for fractions, and compare decimal fractions."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "697F85A12F954712923BD35862DEC2BB", standard_notation: "CCSS.Math.Content.4.NF.B.4c", alt_standard_notation: "4.NF.4.c", description: "Solve word problems involving multiplication of a fraction by a whole number, e.g., by using visual fraction models and equations to represent the problem."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "AD8924A66D334EF59D20C304573728DC", standard_notation: "CCSS.Math.Content.4.NF.B.4b", alt_standard_notation: "4.NF.4.b", description: "Understand a multiple of a/b as a multiple of 1/b, and use this understanding to multiply a fraction by a whole number."},
#   {name: nil, grade: "Grade 4", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-04", api_standard_id: "5AA07FB2E9934DCAB59F4AE0D1624C8B", standard_notation: "CCSS.Math.Content.4.NF.B.4a", alt_standard_notation: "4.NF.4.a", description: "Understand a fraction a/b as a multiple of 1/b."},
