#   {name: nil, grade: "Grade 6", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-06", api_standard_id: "E0D6F554561F4C4EB524449269E39665", standard_notation: "CCSS.Math.Content.6.NS.B", alt_standard_notation: nil, description: "Compute fluently with multi-digit numbers and find common factors and multiples."},
#   {name: nil, grade: "Grade 6", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-06", api_standard_id: "345E28505AB943BFA287EC8FADBF95AD", standard_notation: "CCSS.Math.Content.6.NS.A.1", alt_standard_notation: "6.NS.1", description: "Interpret and compute quotients of fractions, and solve word problems involving division of fractions by fractions, e.g., by using visual fraction models and equations to represent the problem."},
#   {name: nil, grade: "Grade 6", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-06", api_standard_id: "91FABAB899814C55851003A0EE98F8FC", standard_notation: "CCSS.Math.Content.6.NS.A", alt_standard_notation: nil, description: "Apply and extend previous understandings of multiplication and division to divide fractions by fractions."},
#   {name: nil, grade: "Grade 6", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-06", api_standard_id: "D158B010DFE401311D0068A86D17958E", standard_notation: "The Number System", alt_standard_notation: nil, description: "The Number System"},
#   {name: nil, grade: "Grade 6", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-06", api_standard_id: "FA34B81A7B23457B81D873613F7E7B75", standard_notation: "CCSS.Math.Content.6.RP.A.3d", alt_standard_notation: "6.RP.3.d", description: "Use ratio reasoning to convert measurement units; manipulate and transform units appropriately when multiplying or dividing quantities."},
