#   {name: nil, grade: "Grade 8", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-08", api_standard_id: "89F1BFA6E9BE4AFD8537B53D99D16664", standard_notation: "CCSS.Math.Content.8.SP.A.3", alt_standard_notation: "8.SP.3", description: "Use the equation of a linear model to solve problems in the context of bivariate measurement data, interpreting the slope and intercept."},
