#   {name: nil, grade: "Grade 8", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-08", api_standard_id: "29419E561D0F4947929F5400799CB6ED", standard_notation: "CCSS.Math.Content.8.EE.C", alt_standard_notation: nil, description: "Analyze and solve linear equations and pairs of simultaneous linear equations."},
