#   {name: nil, grade: "Grade 8", grade_id: "67810E9EF6944F9383DCC602A3484C23_D10003FB_grade-08", api_standard_id: "16CEBB4B63E8420DAB2020C3BA5A72A7", standard_notation: "CCSS.Math.Content.8.G.A.3", alt_standard_notation: "8.G.3", description: "Describe the effect of dilations, translations, rotations, and reflections on two-dimensional figures using coordinates."},
